import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
//import { Add } from 'material-ui-icons'
import { React, useState, Fragment } from 'react';
import axios from 'axios';
import TablePayment from './TablePayment';
import NedBetalingsPlansListe from './nedbetalinsplanListe';

function Form({ data, setData }) {
    const [open, setOpen] = useState(false);
    const [bAmount, setBAmount] = useState(0);
    const [rate, setRate] = useState(0);
    const [expDate, setExprDate] = useState("");
    const [balanceDate, setBalanceDate] = useState("");
    const [dateFirstPay, setDateFirstPay] = useState("");
    const [list, setList] = useState([])




    function handleClickOpen() {
        setOpen(true)
    }

    function handleClose() {
        setOpen(false)
    }


    //handle submit of POST request
    function handleSubmit(e) {
        handleClose()
        e.preventDefault()

        const options = {
            method: 'POST',
            url: 'https://visningsrom.stacc.com/dd_server_laaneberegning/rest/laaneberegning/v1/nedbetalingsplan',
            headers: { 'Content-type': 'application/json' },
            data: {
                laanebelop: bAmount,
                nominellRente: rate,
                terminGebyr: 30,
                utlopsDato: expDate,
                saldoDato: balanceDate,
                datoForsteInnbetaling: dateFirstPay,
                ukjentVerdi: "TERMINBELOP"

            }
        }

        //send the post request and log the response data
        axios(options).then((response) => {
            console.log(response.data)
            setData(response.data.nedbetalingsplan.innbetalinger)
        }).catch(function (error) {
            console.log(error)
        });
    }

    list.concat(data)
    setList(list)

    return (
        //Modal window by using material ui bootstrap
        //returning a fragment because it takes care of multiple component.
        //Chose the Dialog because it has the necessary property i need
        <Fragment>
            <Button onClick={handleClickOpen}>
                CREATE
             </Button>
            <Dialog open={open} >
                <DialogTitle >Create a new plan</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        Please fill out the form below
          </DialogContentText>
                    <TextField
                        label="Lånebeløp"
                        value={bAmount}
                        onChange={e => setBAmount(e.target.value)}
                    />
                    <br />
                    <TextField
                        label="Rente"
                        value={rate}
                        onChange={e => setRate(e.target.value)}
                    />
                    <br />
                    <TextField
                        label="Utløpsdato"
                        value={expDate}
                        onChange={e => setExprDate(e.target.value)}
                    />
                    <br />
                    <TextField
                        label="SaldoDato"
                        value={balanceDate}
                        onChange={e => setBalanceDate(e.target.value)}
                    />
                    <br />
                    <TextField
                        label="Dato første innbetaling"
                        value={dateFirstPay}
                        onChange={e => setDateFirstPay(e.target.value)}

                    />
                    <br />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleSubmit} color="primary">
                        Create
                    </Button>
                </DialogActions>
            </Dialog>
            {/*  <TablePayment list={nbp}/> */}
            <NedBetalingsPlansListe data={list}/>
        </Fragment>



    )
}
export default Form;
