import React, {useState} from 'react';

function ButtonBar ({setValue, interval, defaultValue}) {
    const [count, setCount] = useState(defaultValue)

    function increment() {
        setCount(count + interval)
        setValue(count + interval)
    }

    function decrement() {
        if (count > 0){
            setCount(count - interval)
        } else {
            setCount(0)
        }

        setValue(count - interval)
    }

    return (
        <div>
            <div>
            <button onClick={decrement}>-</button>
            <input type="text" value={count} defaultValue={defaultValue} onChange={() => {}}></input>
            <button onClick={increment}>+</button>
            </div>
        </div>

    )

}
export default ButtonBar;
